import { createContext, useContext } from 'react';

const LocalStateContext = createContext();
const LocalStateProvider = LocalStateContext.Provider;

function CartStateProvider({ children }) {
  // This is our own custom provider. We will store date(state) and functionality (updaters) in here and anyone can access it via the consumer.

  const [cartOpen, setCartOpen] = false;

  return (
    <LocalStateProvider value={{ cartOpen }}>{children}</LocalStateProvider>
  );
}

// make a custom hook for accessing the cart local state
function useCart() {
  // We use a consumer here to access the lacal state
  const all = useContext(LocalStateContext);
  return all;
}

export { CartStateProvider, useCart };
