import { gql, useMutation } from '@apollo/client';

export const CURRENT_USER_QUERY = gql`
  mutation {
    authenticatedItem {
      ... on User {
        id
        email
        name
      }
    }
  }
`;

export function SignOut() {
  const [authenticatedItem] = useMutation(CURRENT_USER_QUERY);
  return (
    <button
      type="button"
      onClick={() => {
        authenticatedItem.logout();
      }}
    >
      Sign Out
    </button>
  );
}
