import useForm from '../lib/useForm';
import Form from './styles/Form';

export default function CreateProduct() {
  const { inputs, handleChange, resetForm, clearForm } = useForm({
    name: 'Nice Shoes',
    price: 3425,
    description: 'This are the best shoes',
  });

  return (
    <Form>
      <fieldset>
        <label htmlFor="image">
          Name
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>

        <label htmlFor="name">
          Name
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>

        <label htmlFor="price">
          Price
          <input
            type="number"
            name="price"
            id="price"
            placeholder="Price"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>

        {/* <button type="button" onClick={resetForm}>
        Reset Form
      </button>

      <button type="button" onClick={clearForm}>
        Clear Form
      </button> */}

        <button type="submit">+ Add Product</button>
      </fieldset>
    </Form>
  );
}
