import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';

const REMOVE_CART_ITEM_MUTATION = gql`
  mutation REMOVE_CART_ITEM_MUTATION($id: ID!, $) {
    deleteCartItem(id: $id) {
      id
      quantity
    }
  }
`;

// remove product from ui after deleting, without page reload
function update(cache, payload) {
  if (payload.data.deleteCartItem.quantity > 1) {
    return payload.data.deleteCartItem.quantity -= 1;
  }

  cache.evict(cache.identify(payload.data.deleteCartItem));
}

export default function RemoveCartItem({ id }) {
  const [deleteCartItem, { loading }] = useMutation(REMOVE_CART_ITEM_MUTATION, {
    variables: {
      id,
    },
    update,
  });

