import useForm from '../lib/useForm';

export default function CreateProduct() {
  const { inputs, handleChange, resetForm } = useForm({
    name: 'Nice Shoes',
    price: 3425,
    description: 'This are the best shoes',
  });

  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          name="name"
          id="name"
          placeholder="Name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>

      <label htmlFor="price">
        Price
        <input
          type="number"
          name="price"
          id="price"
          placeholder="Price"
          value={inputs.price}
          onChange={handleChange}
        />
      </label>

<button>Clear </button>

    </form>
  );
}
